import { useSelector } from 'react-redux'
import { Link, useNavigate } from "react-router-dom";
import imglogo from "../../images/User/idea_18096067.png";
import imguser from "../../images/User/user.png";

function Sidebar() {
  // get navigate function
  //   const navigate = useNavigate()

  const onLogout = () => {
    // cleat the session storage
    sessionStorage.removeItem("name");
    sessionStorage.removeItem("token");
    sessionStorage.removeItem("email");
    sessionStorage.removeItem("phone");

    // go to login screen
    // navigate('/login')
  };
  return (
    <div
      className="container-fluid"
      style={{ backgroundColor: "", height: "70px", width: "100%" }}
    >
      <div></div>
      <div
        style={{
          height: "800px",
          width: "300px",
          backgroundColor: "#213555",
          marginTop: "20px",
        }}
      >
        {/* Courses */}
        <li
          className="nav-item"
          style={{
            backgroundColor: activeItem === 'Courses' ? '#4F709C' : 'transparent', // Highlight if active
            padding: '10px',
            marginLeft: '40px',
            marginTop: '30px',
            borderRadius: '5px',
          }}
          onClick={() => handleItemClick('Courses')}
        >
          <Link
            className="nav-link"
            style={{
              color: "#D8C4B6",
              textDecoration: 'none',
            }}
            to="/Courses"
          >
            <h4>Courses</h4>
          </Link>
        </li>

        {/* MyCourses */}
        <li
          className="nav-item"
          style={{
            backgroundColor: activeItem === 'MyCourses' ? '#4F709C' : 'transparent',
            padding: '10px',
            marginLeft: '40px',
            marginTop: '10px',
            borderRadius: '5px',
          }}
          onClick={() => handleItemClick('MyCourses')}
        >
          <Link
            className="nav-link"
            style={{
              color: "#D8C4B6",
              textDecoration: 'none',
            }}
            to="/MyCourses"
          >
            <h4>MyCourses</h4>
          </Link>
        </li>

        {/* Certificate */}
        <li
          className="nav-item"
          style={{
            backgroundColor: activeItem === 'Certificate' ? '#4F709C' : 'transparent',
            padding: '10px',
            marginLeft: '40px',
            marginTop: '10px',
            borderRadius: '5px',
          }}
          onClick={() => handleItemClick('Certificate')}
        >
          <Link
            className="nav-link"
            style={{
              color: "#D8C4B6",
              textDecoration: 'none',
            }}
            to="/Certificate"
          >
            <h4>Certificate</h4>
          </Link>
        </li>

        {/* Discussion */}
        <li
          className="nav-item"
          style={{
            backgroundColor: activeItem === 'Discussion' ? '#4F709C' : 'transparent',
            padding: '10px',
            marginLeft: '40px',
            marginTop: '10px',
            borderRadius: '5px',
          }}
          onClick={() => handleItemClick('Discussion')}
        >
          <Link
            className="nav-link"
            style={{
              color: "#D8C4B6",
              textDecoration: 'none',
            }}
            to="/DiscussionPage"
          >
            <h4>Discussion</h4>
          </Link>
        </li>

        {/* Logout */}
        <li
          className="nav-item"
          style={{
            backgroundColor: activeItem === 'Logout' ? '#4F709C' : 'transparent',
            padding: '10px',
            marginLeft: '40px',
            marginTop: '10px',
            borderRadius: '5px',
          }}
          onClick={() => handleItemClick('Logout')}
        >
          <Link
            className="nav-link"
            style={{
              color: "#D8C4B6",
              textDecoration: 'none',
            }}
            to="/login"
          >
            <h4>Logout</h4>
          </Link>
        </li>
      </div>
    </div>
  );
};


export default Sidebar;
