import React, { useState } from 'react';
import { Link, useNavigate } from "react-router-dom";
import imglogo from "../../images/User/idea_18096067.png";
import imguser from "../../images/User/user.png";

function Sidebar() {
  const [selectedItem, setSelectedItem] = useState(null);
  const navigate = useNavigate();

  const onLogout = () => {
    sessionStorage.removeItem("name");
    sessionStorage.removeItem("token");
    sessionStorage.removeItem("email");
    sessionStorage.removeItem("phone");
    navigate('/login');
  };

  const handleItemClick = (item) => {
    setSelectedItem(item);
  };

  return (
    <div className="container-fluid" style={{ backgroundColor: "", height: "70px", width: "100%" }}>
      <div></div>
      <div style={{ height: "800px", width: "300px", backgroundColor: "#213555", marginTop: "20px" }}>
        <li className="nav-item">
          <a
            className="nav-link"
            style={{
              marginLeft: "50px",
              color: selectedItem === 'Courses' ? "#FFFFFF" : "#D8C4B6",
              marginTop: "30px",
              backgroundColor: selectedItem === 'Courses' ? "#4F709C" : "transparent",
              padding: "10px",
              borderRadius: "5px",
            }}
            href="Courses"
            onClick={() => handleItemClick('Courses')}
          >
            <h4>Courses</h4>
          </a>
        </li>
        <li className="nav-item">
          <a
            className="nav-link"
            style={{
              marginLeft: "50px",
              color: selectedItem === 'MyCourses' ? "#FFFFFF" : "#D8C4B6",
              marginTop: "10px",
              backgroundColor: selectedItem === 'MyCourses' ? "#4F709C" : "transparent",
              padding: "10px",
              borderRadius: "5px",
            }}
            href="MyCourses"
            onClick={() => handleItemClick('MyCourses')}
          >
            <h4>MyCourses</h4>
          </a>
        </li>
        <li className="nav-item">
          <a
            className="nav-link"
            style={{
              marginLeft: "50px",
              color: selectedItem === 'Certificate' ? "#FFFFFF" : "#D8C4B6",
              marginTop: "10px",
              backgroundColor: selectedItem === 'Certificate' ? "#4F709C" : "transparent",
              padding: "10px",
              borderRadius: "5px",
            }}
            href="/Certificate"
            onClick={() => handleItemClick('Certificate')}
          >
            <h4>Certificate</h4>
          </a>
        </li>
        <li className="nav-item">
          <a
            className="nav-link"
            style={{
              marginLeft: "50px",
              color: selectedItem === 'Discussion' ? "#FFFFFF" : "#D8C4B6",
              marginTop: "10px",
              backgroundColor: selectedItem === 'Discussion' ? "#4F709C" : "transparent",
              padding: "10px",
              borderRadius: "5px",
            }}
            href="/DiscussionPage"
            onClick={() => handleItemClick('Discussion')}
          >
            <h4>Discussion</h4>
          </a>
        </li>
        <li className="nav-item">
          <a
            className="nav-link"
            style={{
              marginLeft: "50px",
              color: selectedItem === 'LogOut' ? "#FFFFFF" : "#D8C4B6",
              marginTop: "10px",
              backgroundColor: selectedItem === 'LogOut' ? "#4F709C" : "transparent",
              padding: "10px",
              borderRadius: "5px",
            }}
            href="/login"
            onClick={() => { handleItemClick('LogOut'); onLogout(); }}
          >
            <h4>LogOut</h4>
          </a>
        </li>
      </div>
    </div>
  );
}

export default Sidebar;
