import React from 'react';

function Courses() {
  const [cart, setCart] = useState([]);

const courses = [
  { title: "MS.NET", image: "../../imageS/User/net.png" },
  { title: "HTML Complete Course", image: "/image/html.png" },
  { title: "SQL Beginner Course", image: "/image/Mysql.jpg" },
  { title: "JavaScript Beginner Course", image: "/image/js.png" },
  { title: "Java EE", image: "/image/java ee.png" },
  { title: "Operating System", image: "/image/os.jpg" },
  { title: "Python Master Course", image: "/image/python.png" },
  { title: "Java Essentials", image: "/image/java.png" },
  { title: "CSS Complete Course", image: "/image/css.png" }
];

function addToCart(course) {
  // Prevent duplicate courses in the cart
  if (!cart.find((item) => item.id === course.id)) {
    setCart([...cart, course]);
  }
}

function Courses() {
  return (
    <div>
      <div className="header">
        <h2 className="text-center"><b>Courses</b></h2>
      </div>
      <div className="container mt-4">
        <div className="row">
          {courses.map((course, index) => (
            <div key={index} className="col-md-4 mb-4">
              <div className="card p-3 text-center">
                <img src={course.image} alt={course.title} height="100px" />
                <h6 className="mt-2">{course.title}</h6>
                <div className="cost">$49.99</div>
                <button className="mt-3 btn btn-success" onClick={() => addToCart(course)}>Add Course</button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
}
export default Courses;
